import React, { useRef, useState, useEffect } from "react";

import { Carousel, Button, Spin } from "antd";
import server from "../../api/server";
import "./Quiz.css";
import { FinalQuizAnimationFailed } from "../FinalQuizAnimationFailed/FinalQuizAnimationFailed";
import { FinalQuizAnimationSuccess } from "../FinalQuizAnimationSuccess/FinalQuizAnimationSuccess";
import { useSelector } from "react-redux";

const token = localStorage.getItem("token");

const Quiz = props => {
  useEffect(() => {
    getQuestions();
    getAnswers();
    getChapterId();
    // eslint-disable-next-line
  }, []);

  const valuateAnswer = answerIsCorrect => {
    setQuestionIndex(questionIndex => questionIndex + 1);
    if (answerIsCorrect === true) {
      valCountRef.current = valCountRef.current + 1;
    }
    if (valCountRef.current > 3) {
      isCompletedRef.current = true;
    }
    valuateQuiz();
  };

  const valuateQuiz = () => {
    const postTestResults = (
      correct_answers,
      is_Completed,
      userId,
      chapterId,
      userDataId
    ) => {
      console.log("is_Completed", is_Completed);
      console.log("correct_answers", correct_answers);
      console.log("userId", userId);
      console.log("userDataId", userDataId);
      console.log("chapterId", chapterId);
      server
        .post("/chapterdata/", {
          completed: is_Completed,
          correct_answers: correct_answers,
          user: userId,
          userData: userDataId,
          chapter: chapterId
        })
        .then(res => {
          console.log(res);
        });
    };

    if (questionIndex === 5) {
      postTestResults(
        valCountRef.current,
        isCompletedRef.current,
        userData.id,
        chapterId,
        userData.id
      );
    }
  };

  if (questions === undefined || questions.length === 0) {
    return (
      <div className="spinner">
        <Spin size="large" />;
      </div>
    );
  } else {
    return (
      <>
        <div>
          <Carousel ref={ref} className="carouselQuiz">
            {questions.map((question, index) => {
              return question.chapterTitle === currentChapter ? (
                <div key={index}>
                  <h1 className="question-label">{question.label}</h1>
                  {answers.map((answer, index) => {
                    return answer.question === question.id ? (
                      <Button
                        className="answer-buttons"
                        key={answer.id}
                        onClick={() => {
                          valuateAnswer(answer.is_correct);
                          setTimeout(() => ref.current.next(), 300);
                        }}
                      >
                        {answer.text}
                      </Button>
                    ) : null;
                  })}
                </div>
              ) : null;
            })}
          </Carousel>
          {valCountRef.current === 5 && questionIndex === 6 ? (
            <div>
              <div className="quizFailedAnimation">
                <FinalQuizAnimationSuccess
                  validatedCount={valCountRef.current}
                />
              </div>
            </div>
          ) : null}
          {valCountRef.current !== 5 && questionIndex === 6 ? (
            <div>
              <div className="quizFailedAnimation">
                <FinalQuizAnimationFailed
                  validatedCount={valCountRef.current}
                />
              </div>
            </div>
          ) : null}
        </div>
      </>
    );
  }
};

const mapDispatchToProps = dispatch => {
  return {
    getQuiz: () => {
      dispatch(getQuiz());
    }
  };
};

export default Quiz;
