import React, { useEffect, useState, useCallback } from "react";
import { Row } from "antd";
import { useHistory } from "react-router-dom";
import { LoadingOutlined } from "@ant-design/icons";
import { connect } from "react-redux";

import { getChapters } from "../../store/actions/chapter";
import { getUserData } from "../../store/actions/userData";
import CustomCard from "../Card/Card";

import "./Cards.css";

const Cards = props => {
  const history = useHistory();
  const [chapterIds, setChapterIds] = useState({});
  const [completedChapters, setCompletedChapters] = useState([]);

  const chapters = [];

  const sortChapters = () => {
    if (props.chapters === undefined) {
      console.log("loading");
    } else {
      props.chapters.map(item => {
        chaptersLocal.push(item.title);
      });
      console.log(chaptersLocal);
    }
  };
  sortChapters();

  const handleClick = (e, chapter) => {
    console.log("clicked", chapter);
    let currentChapterId = chapterIds[chapter];
    localStorage.setItem("currentChapterId", currentChapterId);
    localStorage.setItem("currentChapter", chapter);
    history.push("/chapter/");
  };

  useEffect(() => {
    props.getChapters();
    props.getUserData();
  }, []);

  return (
    <>
      {props.chapters === undefined ? (
        <LoadingOutlined />
      ) : (
        <div className="container">
          <Row gutter={16} className="cards">
            {props.chapters.map((chapter, index) => {
              if (props.completedChapters.includes(chapter)) {
                console.log("hurray");
              }
            })}
          </Row>
        </div>
      )}
    </>
  );
};

const mapStateToProps = state => {
  return {
    chapters: state.chapterReducer.chapters,
    userData: state.userDataReducer,
    completedChapters: [state.userDataReducer.chapterdata]
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getChapters: () => {
      dispatch(getChapters());
    },
    getUserData: () => {
      dispatch(getUserData());
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Cards);
