import React, { useEffect, useState, useCallback } from "react";
import { Row } from "antd";
import { useHistory } from "react-router-dom";
import { LoadingOutlined } from "@ant-design/icons";
import { connect } from "react-redux";

import { getChapters } from "../../store/actions/chapter";
import CustomCard from "../Card/Card";

import "./Cards.css";

const Cards = props => {
  const history = useHistory();
  const [chapterIds, setChapterIds] = useState({});
  const [chapters, setChapters] = useState([]);
  const [completedChapters, setCompletedChapters] = useState([]);

  const handleClick = (e, chapter) => {
    console.log("clicked", chapter);
    let currentChapterId = chapterIds[chapter];
    localStorage.setItem("currentChapterId", currentChapterId);
    localStorage.setItem("currentChapter", chapter);
    history.push("/chapter/");
  };

  useEffect(() => {
    props.getChapters();
  }, []);

  return (
    <>
      {props.chapters.chapters === undefined ? (
        <LoadingOutlined />
      ) : (
        <div className="container">
          <Row gutter={16} className="cards">
            {chapters.map((chapter, index) => {
              if (completedChapters.includes(chapter)) {
                return (
                  <div className="active-container" key={index}>
                    <CustomCard
                      className="inactive"
                      bordered={true}
                      key={index}
                      index={index + 1}
                      title={chapter}
                      id={chapter.id}
                      hoverable={false}
                    ></CustomCard>
                  </div>
                );
              } else {
                return (
                  <div
                    className="active-container"
                    onClick={e => handleClick(e, chapter)}
                    key={index}
                  >
                    <CustomCard
                      className="active"
                      bordered={true}
                      index={index + 1}
                      title={chapter}
                      id={chapter.id}
                      hoverable={true}
                    ></CustomCard>
                  </div>
                );
              }
            })}
          </Row>
        </div>
      )}
    </>
  );
};

const mapStateToProps = state => {
  return {
    chapters: state.chapterReducer,
    userData: state.userDataReducer,
    completedChapters: [state.userDataReducer.chapterdata]
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getChapters: () => {
      dispatch(getChapters());
    }
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Cards);
